import {
  Button,
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  Form,
  Input,
  Table,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Progress
} from "reactstrap";
import React, { Component } from 'react';
import {Link} from 'react-router-dom';
//import logo from './logo.svg';
import Web3 from 'web3';
import ipfs from './ipfs';
import storehash from './storehash';
//header
//Setting Web3
const web3 = new Web3(window.web3.currentProvider);
class App extends Component {

  constructor(props)
  {
    super(props);
    this.state = {message:'Choose File...',
                  percent: 10};
  }
  generateRand = () => {
    const min = 1;
    const max = 100;
    const rand = min + Math.random() * (max - min);
    this.setState({ random: this.state.percent+ rand });
  };
  updateProgress = () => {
    this.setState({ percent: this.state.percent+10})
  };

    state = {
      ipfsHash:null,
      buffer:'',
      ethAddress:'',
      blockNumber:'',
      transactionHash:'',
      gasUsed:'',
      txReceipt: ''   
    };

    captureFile =(event) => {
        event.stopPropagation()
        event.preventDefault()
        const file = event.target.files[0]
        let  value = event.target.value,
        message;
        message = value.split( '\\' ).pop();
        if(message) this.setState({...this.state,message});
        let reader = new window.FileReader()
        reader.onloadend = () => this.convertToBuffer(reader)    
        reader.readAsArrayBuffer(file)
      };

    convertToBuffer = async(reader) => {
      //file is converted to a buffer to prepare for uploading to IPFS
        const buffer = await Buffer.from(reader.result);
      //set this buffer -using es6 syntax
        this.setState({buffer});
    };

    onClick = async () => {

    try{
        this.setState({blockNumber:"waiting.."});
        this.setState({gasUsed:"waiting..."});

        // get Transaction Receipt in console on click
        // See: https://web3js.readthedocs.io/en/1.0/web3-eth.html#gettransactionreceipt
        await web3.eth.getTransactionReceipt(this.state.transactionHash, (err, txReceipt)=>{
          console.log(err,txReceipt);
          this.setState({txReceipt});
        }); //await for getTransactionReceipt

        await this.setState({blockNumber: this.state.txReceipt.blockNumber});
        await this.setState({gasUsed: this.state.txReceipt.gasUsed});    
      } //try
    catch(error){
        console.log(error);
      } //catch
  } //onClick

    onSubmit = async (event) => {
      event.preventDefault();
      if (window.ethereum) {
        window.web3 = new Web3(window.ethereum);
        try {
            // Request account access if needed
            await window.ethereum.enable();
            // Acccounts now exposed

                 //bring in user's metamask account address
                const accounts = await web3.eth.getAccounts();

                console.log('Sending from Metamask account: ' + accounts[0]);

                //obtain contract address from storehash.js
                const ethAddress= await storehash.options.address;
                this.setState({ethAddress});

                //save document to IPFS,return its hash#, and set hash# to state
                //https://github.com/ipfs/interface-ipfs-core/blob/master/SPEC/FILES.md#add 
                await ipfs.add(this.state.buffer, (err, ipfsHash) => {
                  console.log(err,ipfsHash);
                  //setState by setting ipfsHash to ipfsHash[0].hash 
                  this.setState({ ipfsHash:ipfsHash[0].hash });

                  // call Ethereum contract method "sendHash" and .send IPFS hash to etheruem contract 
                  //return the transaction hash from the ethereum contract
                  //see, this https://web3js.readthedocs.io/en/1.0/web3-eth-contract.html#methods-mymethod-send

                  storehash.methods.sendHash(this.state.ipfsHash*10000000000).send({
                    from: accounts[0] 
                  }, (error, transactionHash) => {
                    console.log(transactionHash);
                    this.setState({transactionHash});
                  }); //storehash 
                }) //await ipfs.add 


        } catch (error) {
            alert("Permission to Access MetaMask Denied");
        }
    }
    // Legacy dapp browsers...
    else if (window.web3) {
        window.web3 = new Web3(web3.currentProvider);
        // Acccounts always exposed
        web3.eth.sendTransaction({/* ... */});
    }
    // Non-dapp browsers...
    else {
        console.log('Non-Ethereum browser detected. You should consider trying MetaMask!');
    }


    }; //onSubmit 

    render() {

      return (
        <div>
        <Container>
          <Row>
            <Card className="card-signup" data-background-color="blue">
              <Form action="" className="form form-group" onSubmit={this.onSubmit}>
                <CardHeader className="text-center">
                  <CardTitle className="title-up" tag="h3">
                    Upload
                  </CardTitle>
                  <div className="social-line">
                    <Button
                      className="btn-neutral btn-icon btn-round"
                      color="github"
                      onClick={e => e.preventDefault()}
                      size="lg"
                    >
                      <i className="now-ui-icons arrows-1_cloud-upload-94"></i>
                    </Button>
                  </div>
                </CardHeader>
                <CardBody>
                  <InputGroup>
                    <InputGroupText text-align="center" style={{"width" : "100%"}}>
                    {this.state.message}
                      </InputGroupText>
                    <Input
                      type="file"
                      className="form-control"
                      onChange = {this.captureFile}
                    ></Input>
                  </InputGroup>
                </CardBody>
                <CardFooter className="text-center">
                  <Button
                    className="btn-neutral btn-round"
                    color="info"
                    type="submit"
                    size="lg"
                  >
                    Submit
                  </Button>

              <div className="progress-container progress-neutral" style={{margin:'1%','margin-top':'20px'}}>
              <span className="progress-badge">Uploading</span>
                <Progress animated max="100" value={this.state.percent} onClick={(e) => {if(this.state.percent <=99) {this.updateProgress()} else {e.preventDefault()} }} >
                  <span className="progress-value">{this.state.percent}%</span>
                </Progress>
              </div>
                </CardFooter>
              </Form>

            </Card>
          </Row>
          <hr/>
            <Button onClick = {this.onClick}> Get Transaction Receipt </Button>
              <Table bordered responsive>
                <thead>
                  <tr>
                    <th>Tx Receipt Category</th>
                    <th>Values</th>
                  </tr>
                </thead>

                <tbody>
                  <tr>
                    <td>IPFS Hash # stored on Eth Contract</td>
                    <td>{this.state.ipfsHash}</td>
                  </tr>
                  <tr>
                    <td>Ethereum Contract Address</td>
                    <td>{this.state.ethAddress}</td>
                  </tr>

                  <tr>
                    <td>Tx Hash # </td>
                    <td>{this.state.transactionHash}</td>
                  </tr>

                  <tr>
                    <td>Block Number # </td>
                    <td>{this.state.blockNumber}</td>
                  </tr>

                  <tr>
                    <td>Gas Used</td>
                    <td>{this.state.gasUsed}</td>
                  </tr>                
                </tbody>
            </Table>
        </Container>
    </div>
      );
    } //render
}

export default App;
