//overrides metamask v0.2 for our 1.0 version. 
//1.0 lets us use async and await instead of promises
import Web3 from 'web3';

function web3()
{
try
{
const web3 = new Web3(window.web3.currentProvider);
}
catch(error)
{
    console.log(error);
}

}
export default web3;
